"""
title: Save Code to File
author: Genie Sim Team
version: 0.1.0
required_open_webui_version: ">=0.3.0"
description: An action to save code blocks from messages to a local directory.
icon_url: data:image/png;base64,
"""

import os
import json
from pathlib import Path
from datetime import datetime
from pydantic import BaseModel, Field
from typing import Optional
import aiofiles


class Action:
    """
    Action to save code blocks from a message to a specified local directory.
    """

    def __init__(self):
        self.valves = self.Valves()
        # Initialize save directory
        self._ensure_save_dir()

    class Valves(BaseModel):
        """
        Configurable valve parameters that will be displayed as settings in the OpenWebUI management interface.
        """

        # Root directory for saving files (absolute path)
        save_root_dir: str = Field(
            default="/geniesim/generator/",
            description="Root directory path where saved code files will be stored,  default: /geniesim/generator/",
        )
        # file name
        file_name: str = Field(
            default="LLM_RESULT.py",
            description="Name of the file to save, default LLM_RESULT.py",
        )
        # Auto-confirm save (no confirmation dialog)
        auto_confirm: bool = Field(
            default=False,
            description="When enabled, clicking the button will save directly without requiring user confirmation. default: False",
        )

    def _ensure_save_dir(self):
        """Ensure the save directory exists"""
        Path(self.valves.save_root_dir).mkdir(parents=True, exist_ok=True)

    async def action(
        self,
        body: dict,
        __user__: Optional[dict] = None,
        __event_emitter__=None,
        __event_call__=None,
        **kwargs,
    ) -> dict:
        """
        Main execution method. Extracts code, saves to file, and returns result.
        """
        # 1. Get current message content
        message = body
        keys = message.keys()
        last_message = message.get("messages", None)
        if not last_message:
            if __event_emitter__:
                await __event_emitter__(
                    {
                        "type": "notification",
                        "data": {
                            "type": "warning",
                            "content": "No messages to save",
                        },
                    }
                )
            return {"content": "(No messages to save.)"}
        last_message = last_message[-1]
        last_message_content = last_message.get("content", "")
        code_blocks = self._extract_code_blocks(last_message_content)
        if not code_blocks:
            if __event_emitter__:
                await __event_emitter__(
                    {
                        "type": "notification",
                        "data": {
                            "type": "warning",
                            "content": "No code blocks found in this message.",
                        },
                    }
                )
            return {"content": "(No code blocks found in this message to save.)"}
        # 3. If user confirmation is required, show confirmation dialog
        if not self.valves.auto_confirm and __event_call__:
            confirm = await __event_call__(
                {
                    "type": "confirmation",
                    "data": {
                        "title": "Confirm Save Code",
                        "message": f"Code blocks found. Save to local?",
                        "confirm_text": "Save",
                        "cancel_text": "Cancel",
                    },
                }
            )
            if not confirm:
                # User cancelled the operation
                if __event_emitter__:
                    await __event_emitter__(
                        {
                            "type": "notification",
                            "data": {
                                "type": "info",
                                "content": "Save operation cancelled.",
                            },
                        }
                    )
                return {"content": "(User cancelled the save operation.)"}

        # 4. Start processing and saving files
        if __event_emitter__:
            await __event_emitter__(
                {
                    "type": "status",
                    "data": {"description": f"Saving code blocks..."},
                }
            )

        saved_files_info = []
        # Save only the last one
        code_blocks = code_blocks[-1:]
        file_info = await self._save_single_code_block(code_blocks[0][1], code_blocks[0][0])
        if file_info:
            saved_files_info.append(file_info)
            save_path_str = file_info["path"]
            if save_path_str.startswith("/"):
                save_path_str = save_path_str[1:]
            if __event_emitter__:
                await __event_emitter__(
                    {
                        "type": "notification",
                        "data": {
                            "type": "success",
                            "content": f"Successfully saved file to {save_path_str}.",
                        },
                    }
                )

            return {"content": f"Successfully saved {len(saved_files_info)} file(s) to {file_info['path']}."}
        else:
            if __event_emitter__:
                await __event_emitter__(
                    {
                        "type": "notification",
                        "data": {
                            "type": "error",
                            "content": "Failed to save file.",
                        },
                    }
                )
            return {"content": "(Failed to save file.)"}

    def _extract_code_blocks(self, content: str) -> list:
        """
        Extract all code blocks from message content.
        Returns a list where each element is a tuple of (language, code content).
        """
        blocks = []
        lines = content.split("\n")
        i = 0

        while i < len(lines):
            if lines[i].startswith("```"):
                # Get language marker (remove leading ```)
                lang = lines[i][3:].strip()
                code_lines = []
                i += 1
                # Collect code content until encountering closing ```
                while i < len(lines) and not lines[i].startswith("```"):
                    code_lines.append(lines[i])
                    i += 1
                if code_lines:  # Ensure code block is not empty
                    blocks.append((lang or "txt", "\n".join(code_lines)))
            i += 1
        return blocks

    async def _save_single_code_block(self, code_content: str, language: str) -> Optional[dict]:
        """
        Save a single code block to file.
        """
        try:
            # Build target file path
            save_path = self.valves.save_root_dir
            save_path += "/" + self.valves.file_name if not save_path.endswith("/") else self.valves.file_name

            # Asynchronously write file and change permissions
            async with aiofiles.open(save_path, "w", encoding="utf-8") as f:
                await f.write(code_content)
            # Change file permissions to 777
            os.chmod(save_path, 0o777)
            return {
                "path": str(save_path),
                "language": language,
                "size": len(code_content),
            }

        except Exception as e:
            return None
